package String;

public class uppercasrlowercase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str1= "this is puja";
		String str2= "HI I AM POOJA";
		
		System.out.println("Str is:"+str1+"");
		System.out.println("Str1 lower case ;"+str1.toUpperCase()+"");
		System.out.println("Str2 is:"+str2+"");
		System.out.println("Str lower case ;"+str1.toUpperCase()+"");
		
		

	}

}
